package hoodland.opensource.koarsegrind

import hoodland.opensource.toolbox.InlineImage
import hoodland.opensource.toolbox.quickTimestamp
import hoodland.opensource.toolbox.UNSET_STRING
import java.io.File
import java.nio.file.FileSystems
import java.nio.file.Files
import java.nio.file.LinkOption

// These are global variables and functions for the Koarse Grind test framework.
// In C#/Java there is a CoarseGrind static class they belong to.
// In Kotlin it makes more sense to just have these be properly global with internal scope.

// public static string DateFormatString = "yyyy-MM-dd HH-mm-ss.ffff"; - This matches the one in Memoir
internal const val SUMMARY_FILE = "SUMMARY"
internal const val SUMMARY_FILE_EXTENSION = ".csv"
internal const val SUMMARY_FILE_NAME = SUMMARY_FILE + SUMMARY_FILE_EXTENSION
internal const val SUMMARY_TEXTFILE_NAME = "." + SUMMARY_FILE // Single line stating the overall value. This file is hidden on Linux/Unix/macOS.

internal const val CATEGORY_PATH_DELIMITER = '|'
internal const val TOP_LEVEL = "(top level)"

internal var KILL_SWITCH: Boolean = false

internal val HEADER_ICON = InlineImage(
        "",
        "png"
).toString()

internal fun logHeader(title: String): String {
    return "<table style=\"margin-left: 0; margin-right: 0\"><tr><td>\r\n\r\n$HEADER_ICON\r\n\r\n</td><td><h1>$title</h1>\r\nPowered by Koarse Grind\r\n&nbsp;&nbsp;<small><i>Initiated $quickTimestamp</i></small></td></tr></table>\r\n<hr>\r\n\r\n"
}

// This is calculated on the fly the first time it is called.
// If a "Documents" folder exists off the user's home folder, the Test Results folder goes there.
// Otherwise it goes in the user's home folder. A test programmer may override this default by
// setting the defaultParentFolder field before calling TestProgram.run().
public var defaultParentFolder = UNSET_STRING
internal val DEFAULT_PARENT_FOLDER: String
    get() {
        if (defaultParentFolder == UNSET_STRING) {
            val rootShortName = File.separatorChar + "Test Results"
            var rootPath = "${System.getProperty("user.home")}${File.separatorChar}Documents"

            if (Files.notExists(FileSystems.getDefault().getPath(rootPath), LinkOption.NOFOLLOW_LINKS)) {
                rootPath = System.getProperty("user.home")
            }

            defaultParentFolder = rootPath + rootShortName
        }

        return defaultParentFolder
    }